mapper.select:
		select `id`,`weixin`,`phone`,`principal`,`company_id`,`gongcheng_class`,`deleted`,`update_time`,`create_time`,`gongcheng_name`,`gongcheng_content`,`url` from company_gongcheng
        where deleted=0
		  		   and id = #{id} 
		  		   and weixin = #{weixin} 
		  		   and phone = #{phone} 
		  		   and principal = #{principal} 
		  		   and company_id = #{companyId} 
		  		   and gongcheng_class = #{gongchengClass} 
		  		   and deleted = #{deleted} 
		  		   and update_time = #{updateTime} 
		  		   and create_time = #{createTime} 
		  		   and gongcheng_name = #{gongchengName} 
		  		   and gongcheng_content = #{gongchengContent} 
		  		   and url = #{url} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*) from company_gongcheng
 where deleted=0
